# hr_app/hr/templatetags/form_tags.pyfrom django import templatefrom datetime import timedeltaregister = template.Library()@register.filterdef add_class(field, css_class):    """Add a CSS class to a form field's widget."""    existing_classes = field.field.widget.attrs.get('class', '')    new_classes = f"{existing_classes} {css_class}".strip()    return field.as_widget(attrs={'class': new_classes})@register.filterdef add_days(value, days):    return value + timedelta(days=int(days))@register.filterdef get_item(dictionary, key):    return dictionary.get(key, 0)  # Default to 0 if key not found